import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import CreatePollForm from "../components/CreatePollForm";
import ResultsChart from "../components/ResultsChart";

const socket = io("http://localhost:5000");

export default function TeacherDashboard() {
  const [currentPoll, setCurrentPoll] = useState(null);
  const [pollHistory, setPollHistory] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [showParticipants, setShowParticipants] = useState(false);

  // Fetch poll history and current poll
  useEffect(() => {
    const fetchPolls = async () => {
      try {
        const res = await fetch("http://localhost:5000/polls");
        const data = await res.json();
        setPollHistory(data.history || []);
        setCurrentPoll(data.current || null);
        setParticipants(data.current?.participants || []);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPolls();
  }, []);

  // Socket.io for real-time updates
  useEffect(() => {
    socket.on("newPoll", (poll) => {
      setCurrentPoll(poll);
      setParticipants(poll.participants || []);
    });

    socket.on("pollResults", (poll) => {
      setCurrentPoll(poll);
      setParticipants(poll.participants || []);
    });

    socket.on("newParticipant", (name) => {
      setParticipants((prev) => {
        if (!prev.includes(name)) return [...prev, name];
        return prev;
      });
    });

    return () => {
      socket.off("newPoll");
      socket.off("pollResults");
      socket.off("newParticipant");
    };
  }, []);

  return (
    <div style={{ maxWidth: "800px", margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center" }}>Teacher Dashboard</h1>

      <CreatePollForm />

      {currentPoll && (
        <div style={{ marginTop: "30px" }}>
          <h2>Current Poll</h2>
          <p><strong>Question:</strong> {currentPoll.question}</p>

          <div style={{ maxWidth: "500px", margin: "20px 0" }}>
            <ResultsChart options={currentPoll.options} />
          </div>

          {/* Participants Button */}
          <button
            onClick={() => setShowParticipants(!showParticipants)}
            style={{
              padding: "8px 16px",
              backgroundColor: "#6c757d",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Participants ({participants.length})
          </button>

          {showParticipants && (
            <ul style={{ marginTop: "10px", padding: "10px", border: "1px solid #ccc", borderRadius: "8px" }}>
              {participants.length === 0 ? (
                <li>No participants yet</li>
              ) : (
                participants.map((name, idx) => <li key={idx}>{name}</li>)
              )}
            </ul>
          )}
        </div>
      )}

      {/* Poll History */}
      {pollHistory.length > 0 && (
        <div style={{ marginTop: "40px" }}>
          <h2>Past Polls</h2>
          <ul>
            {pollHistory.map((poll, idx) => (
              <li key={idx}>
                <strong>{poll.question}</strong> - Participants: {poll.participants?.length || 0}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
