import React, { useState, useEffect } from "react";
import CreatePollForm from "../components/CreatePollForm";
import ResultsChart from "../components/ResultsChart";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000"); // backend URL

export default function TeacherDashboard() {
  const [poll, setPoll] = useState(null);
  const [addQuestion, setAddQuestion] = useState(false); // toggle form
  const [results, setResults] = useState([]);

  // Listen for live updates
  useEffect(() => {
    socket.on("newPoll", (data) => {
      setPoll(data);
      setResults(data.options);
    });

    socket.on("pollResults", (data) => {
      setResults(data);
    });

    return () => {
      socket.off("newPoll");
      socket.off("pollResults");
    };
  }, []);

  // Create poll via API
  const handleCreatePoll = async (pollData) => {
    try {
      const res = await fetch("http://localhost:5000/polls", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(pollData),
      });
      if (!res.ok) throw new Error("Failed to create poll");
      const data = await res.json();
      setPoll(data.poll);
      setAddQuestion(false); // hide form after creating poll
    } catch (err) {
      console.error(err);
      alert("Error creating poll");
    }
  };

  return (
    <div className="teacher-dashboard">
      <h1>Teacher Dashboard</h1>

      <button onClick={() => setAddQuestion(!addQuestion)}>
        {addQuestion ? "Hide Question Form" : "Add Question"}
      </button>

      {addQuestion && <CreatePollForm onCreate={handleCreatePoll} />}

      {poll && (
        <div className="poll-results">
          <h2>Current Poll:</h2>
          <p>{poll.question}</p>
          <ResultsChart options={results} />
        </div>
      )}
    </div>
  );
}
