import React, { useState } from 'react';
import TeacherDashboard from './pages/TeacherDashboard';
import StudentJoin from './pages/StudentJoin';

export default function App(){
  const [mode, setMode] = useState(null);
  return (
    <div style={{ padding: 20, fontFamily: 'Inter, Arial' }}>
      <h1>Live Polling â€” Intervue Assignment</h1>
      {!mode && (
        <div style={{ display: 'flex', gap: 12 }}>
          <button onClick={()=>setMode('teacher')}>Teacher</button>
          <button onClick={()=>setMode('student')}>Student</button>
        </div>
      )}
      {mode==='teacher' && <TeacherDashboard onBack={()=>setMode(null)} />}
      {mode==='student' && <StudentJoin onBack={()=>setMode(null)} />}
    </div>
  );
}
