import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import PollRoom from './PollRoom';

const socket = io('http://localhost:4000');

export default function StudentJoin({ onBack }){
  const [pollId, setPollId] = useState('');
  const [name, setName] = useState(() => sessionStorage.getItem('studentName') || '');
  const [joined, setJoined] = useState(false);

  useEffect(()=> {
    return () => { /* cleanup if needed */ };
  },[]);

  const join = () => {
    if (!name.trim()) return alert('Enter your name (stored per tab)');
    sessionStorage.setItem('studentName', name);
    socket.emit('studentJoin', { pollId, name });
    setJoined(true);
  };

  return (
    <div>
      <button onClick={onBack}>Back</button>
      {!joined ? (
        <>
          <h2>Join Poll</h2>
          <input placeholder="Poll ID" value={pollId} onChange={e=>setPollId(e.target.value)} />
          <input placeholder="Your name (per tab only)" value={name} onChange={e=>setName(e.target.value)} />
          <button onClick={join}>Join</button>
        </>
      ) : <PollRoom socket={socket} pollId={pollId} name={name} />}
    </div>
  );
}
