import React, { useState, useEffect } from "react";
import CreatePollForm from "../components/CreatePollForm";
import ResultsChart from "../components/ResultsChart";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

export default function TeacherDashboard() {
  const [poll, setPoll] = useState(null);
  const [addQuestion, setAddQuestion] = useState(false);
  const [results, setResults] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    socket.on("newPoll", (data) => { setPoll(data); setResults(data.options); });
    socket.on("pollResults", (data) => setResults(data));
    socket.on("pollEnded", () => { setPoll(null); fetchHistory(); });
    fetchHistory();
    return () => { socket.off("newPoll"); socket.off("pollResults"); socket.off("pollEnded"); };
  }, []);

  const fetchHistory = async () => {
    const res = await fetch("http://localhost:5000/polls/history");
    if (res.ok) setHistory(await res.json());
  };

  const handleCreatePoll = async (pollData) => {
    const res = await fetch("http://localhost:5000/polls", {
      method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(pollData)
    });
    if (res.ok) setPoll((await res.json()).poll);
    setAddQuestion(false);
  };

  const handleEndPoll = () => socket.emit("endPoll");

  return (
    <div>
      <h1>Teacher Dashboard</h1>
      <button onClick={() => setAddQuestion(!addQuestion)}>
        {addQuestion ? "Hide Question Form" : "Add Question"}
      </button>
      {addQuestion && <CreatePollForm onCreate={handleCreatePoll} />}

      {poll && (
        <div>
          <h2>Current Poll:</h2>
          <p>{poll.question}</p>
          <ResultsChart options={results} />
          <p><strong>Participants:</strong> {poll.participants.join(", ")}</p>
          <button onClick={handleEndPoll}>End Poll</button>
        </div>
      )}

      {history.length > 0 && (
        <div>
          <h2>Poll History</h2>
          {history.map((p) => (
            <div key={p.id} style={{ border: "1px solid #ccc", margin: 10, padding: 5 }}>
              <p><strong>Question:</strong> {p.question}</p>
              <ResultsChart options={p.options} />
              <p><strong>Participants:</strong> {p.participants.join(", ")}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
