import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import CreatePollForm from '../components/CreatePollForm';
import ResultsChart from '../components/ResultsChart';

const socket = io('http://localhost:4000');

export default function TeacherDashboard({ onBack }){
  const [poll, setPoll] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [students, setStudents] = useState([]);
  const [current, setCurrent] = useState(null);
  const [timeLimit, setTimeLimit] = useState(60);

  useEffect(()=>{
    socket.on('teacherState', (s) => {
      setPoll({ id: s.pollId, title: s.title});
      setQuestions(s.questions || []);
      setCurrent(s.currentQuestion || null);
    });
    socket.on('studentsUpdate', (s) => setStudents(s));
    socket.on('questionStarted', q => setCurrent(q.question));
    socket.on('partialUpdate', d => setCurrent(prev => ({...prev, options: d.options})));
    socket.on('questionEnded', ({ results }) => {
      setCurrent(null);
      // store history locally or refetch
    });
    return ()=> socket.off();
  },[]);

  // create poll (calls backend /polls) then join as teacher
  const createPoll = async ({ title, timeLimitSec }) => {
    const r = await fetch('http://localhost:4000/polls', { method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({ title, timeLimitSec })});
    const p = await r.json();
    setPoll(p);
    setTimeLimit(p.timeLimitSec || 60);
    socket.emit('teacherJoin', { pollId: p.id });
  };

  const addQuestion = async (text, options) => {
    if (!poll) return alert('Create poll first');
    const res = await fetch(`http://localhost:4000/polls/${poll.id}/questions`, {
      method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify({ text, options })
    });
    const q = await res.json();
    setQuestions(prev=>[...prev, q]);
  };

  const askQuestion = (questionId) => {
    socket.emit('askQuestion', { pollId: poll.id, questionId, customTime: timeLimit });
  };

  const endNow = () => socket.emit('endQuestionNow', { pollId: poll.id });

  const removeStudent = (socketId) => socket.emit('removeStudent', { pollId: poll.id, studentSocketId: socketId });

  return (
    <div>
      <button onClick={onBack}>Back</button>
      {!poll && <CreatePollForm onCreate={createPoll} />}
      {poll && (
        <>
          <h2>{poll.title} â€” ID: {poll.id}</h2>
          <div>
            <label>Time limit (sec): <input type="number" value={timeLimit} onChange={e=>setTimeLimit(Number(e.target.value))} /></label>
          </div>
          <h3>Questions</h3>
          <ul>
            {questions.map(q=>(
              <li key={q.id}>
                {q.text} <button disabled={!!current} onClick={()=>askQuestion(q.id)}>Ask</button>
              </li>
            ))}
          </ul>
          <h3>Students ({students.length})</h3>
          <ul>
            {students.map(s=> <li key={s.id}>{s.name} <button onClick={()=>removeStudent(s.id)}>Remove</button></li>)}
          </ul>

          {current ? (
            <>
              <h3>Active Question</h3>
              <div>{current.text}</div>
              <ResultsChart options={current.options}/>
              <div><button onClick={endNow}>End Now</button></div>
            </>
          ) : <div>No active question</div>}
        </>
      )}
    </div>
  );
}
