import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import ResultsChart from "../components/ResultsChart";

const socket = io("http://localhost:5000");

export default function PollRoom() {
  const [poll, setPoll] = useState(null);
  const [selectedOption, setSelectedOption] = useState("");
  const [results, setResults] = useState([]);
  const [pollEnded, setPollEnded] = useState(false);
  const [participants, setParticipants] = useState([]);

  const studentName = sessionStorage.getItem("studentName");

  useEffect(() => {
    if (!studentName) return;

    // Listen for polls and results
    socket.on("newPoll", (data) => {
      setPoll(data);
      setResults(data.options);
      setParticipants(data.participants || []);
      setPollEnded(false);
      setSelectedOption("");
      // Join this poll
      socket.emit("joinPoll", { pollId: data.id, name: studentName });
    });

    socket.on("pollResults", (data) => {
      setResults(data.options);
      if (data.votesByStudent) setParticipants(Object.keys(data.votesByStudent));
    });

    socket.on("pollEnded", () => setPollEnded(true));

    socket.on("newParticipant", (name) => {
      setParticipants((prev) => [...new Set([...prev, name])]);
    });

    return () => {
      socket.off("newPoll");
      socket.off("pollResults");
      socket.off("pollEnded");
      socket.off("newParticipant");
    };
  }, [studentName]);

  const handleSubmit = () => {
    if (!selectedOption) return alert("Please select an option");
    socket.emit("submitAnswer", {
      pollId: poll.id,
      studentName,
      selectedOption,
    });
  };

  if (!poll) return <p style={{ textAlign: "center" }}>Waiting for a poll...</p>;

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>{poll.question}</h2>

      <div style={{ display: "flex", flexDirection: "column", gap: "10px", marginBottom: "15px" }}>
        {poll.options.map((opt, idx) => (
          <button
            key={idx}
            onClick={() => setSelectedOption(opt.text)}
            style={{
              padding: "10px",
              cursor: "pointer",
              backgroundColor: selectedOption === opt.text ? "#007bff" : "#f0f0f0",
              color: selectedOption === opt.text ? "#fff" : "#000",
              border: "1px solid #ccc",
              borderRadius: "5px",
            }}
          >
            {opt.text}
          </button>
        ))}
      </div>

      {!pollEnded && (
        <button
          onClick={handleSubmit}
          style={{ padding: "8px 16px", cursor: "pointer" }}
        >
          Submit Answer
        </button>
      )}

      <h3 style={{ marginTop: "20px" }}>Participants:</h3>
      <ul>
        {participants.map((name, idx) => (
          <li key={idx}>{name}</li>
        ))}
      </ul>

      {pollEnded && (
        <div style={{ marginTop: "20px" }}>
          <h3>Poll Results</h3>
          <ResultsChart options={results} />
        </div>
      )}
    </div>
  );
}
