import React, { useEffect, useState, useRef } from "react";
import { io } from "socket.io-client";
import ResultsChart from "../components/ResultsChart";

const socket = io("http://localhost:5000");

export default function PollRoom() {
  const [poll, setPoll] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [results, setResults] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  const timerRef = useRef(null);
  const studentName = localStorage.getItem("studentName");

  // Fetch current poll
  useEffect(() => {
    const fetchPoll = async () => {
      try {
        const res = await fetch("http://localhost:5000/polls/current");
        if (!res.ok) return;
        const data = await res.json();
        setPoll(data);
        setResults(data.options);
        setTimeLeft(data.timeLimit || 60);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPoll();
  }, []);

  // Socket.io events
  useEffect(() => {
    socket.on("newPoll", (data) => {
      setPoll(data);
      setResults(data.options);
      setSubmitted(false);
      setSelectedOption(null);
      setTimeLeft(data.timeLimit || 60);
    });

    socket.on("pollResults", (data) => setResults(data));

    return () => {
      socket.off("newPoll");
      socket.off("pollResults");
    };
  }, []);

  // Countdown timer
  useEffect(() => {
    if (!poll || submitted) return;

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          setSubmitted(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timerRef.current);
  }, [poll, submitted]);

  const handleSubmit = () => {
    if (selectedOption === null) {
      alert("Please select an option");
      return;
    }
    socket.emit("submitVote", { studentName, optionIndex: selectedOption });
    setSubmitted(true);
    clearInterval(timerRef.current);
  };

  if (!studentName) return <p>Please enter your name first!</p>;
  if (!poll) return <p>Waiting for a poll...</p>;

  return (
    <div>
      <h1>Poll Room</h1>
      <p>{poll.question}</p>

      {!submitted ? (
        <div>
          <p>Time Left: {timeLeft}s</p>
          {poll.options.map((opt, idx) => (
            <div key={idx}>
              <label>
                <input
                  type="radio"
                  name="option"
                  value={idx}
                  checked={selectedOption === idx}
                  onChange={() => setSelectedOption(idx)}
                  disabled={timeLeft === 0}
                />
                {opt.text}
              </label>
            </div>
          ))}
          <button onClick={handleSubmit} disabled={selectedOption === null || timeLeft === 0}>
            Submit Answer
          </button>
        </div>
      ) : (
        <div>
          <h3>Live Results:</h3>
          <ResultsChart options={results} />
        </div>
      )}
    </div>
  );
}
