import React, { useEffect, useState } from 'react';
import ResultsChart from '../components/ResultsChart';

export default function PollRoom({ socket, pollId, name }){
  const [current, setCurrent] = useState(null);
  const [answered, setAnswered] = useState(false);
  const [selected, setSelected] = useState(null);
  const [timeLeft, setTimeLeft] = useState(null);

  useEffect(()=>{
    socket.on('joined', (s) => {
      // s.currentQuestion may be present
      if (s.currentQuestion) {
        setCurrent(s.currentQuestion);
      }
    });
    socket.on('questionStarted', ({ question, timeLimitSec }) => {
      setCurrent(question);
      setAnswered(false);
      setSelected(null);
      setTimeLeft(timeLimitSec);
      // start client countdown
      const timer = setInterval(()=> {
        setTimeLeft(prev => prev > 0 ? prev-1 : 0);
      }, 1000);
      return ()=> clearInterval(timer);
    });
    socket.on('partialUpdate', d => setCurrent(prev => ({...prev, options: d.options})));
    socket.on('questionEnded', ({ results }) => {
      setCurrent(null);
      setAnswered(true);
    });
    socket.on('removed', () => alert('You were removed by teacher'));
    return ()=> socket.off();
  },[socket]);

  const submit = () => {
    if (!current) return;
    if (answered) return;
    if (selected === null) return alert('select an option');
    socket.emit('submitAnswer', { pollId, questionId: current.id, optionId: selected });
    setAnswered(true);
  };

  return (
    <div>
      <h3>Poll {pollId}</h3>
      {current ? (
        <div>
          <h4>{current.text}</h4>
          {current.options.map(o=>(
            <div key={o.id}>
              <label><input type="radio" name="opt" onChange={()=>setSelected(o.id)} disabled={answered} /> {o.text} ({o.votes})</label>
            </div>
          ))}
          <div>Time left: {timeLeft ?? '—'}</div>
          <button onClick={submit} disabled={answered}>Submit</button>
          <ResultsChart options={current.options} />
        </div>
      ) : (
        <div>
          {answered ? <p>Results shown above (question ended)</p> : <p>Waiting for teacher to ask question</p>}
        </div>
      )}
    </div>
  );
}
